create schema "metalake_demo.catalog_hive".sales with (location = 'hdfs://hive:9000/user/hive/warehouse/sales.db');

CREATE TABLE  "metalake_demo.catalog_hive".sales.categories (category_id int,category_name varchar(100));

CREATE TABLE "metalake_demo.catalog_hive".sales.customers (customer_id int, customer_name varchar(100), customer_email varchar(100));

CREATE TABLE "metalake_demo.catalog_hive".sales.products (product_id int, category_id int, product_name varchar(100), price decimal(10,2));

CREATE TABLE "metalake_demo.catalog_hive".sales.sales (sale_id int, employee_id int, store_id int, product_id int, customer_id int, sold date, quantity int, total_amount decimal(10,2));

CREATE TABLE "metalake_demo.catalog_hive".sales.stores (store_id int, store_name varchar(100), location varchar(100));

